  `que_text` text NOT NULL COMMENT 'โจทย์',
  `que_opt_a` varchar(255) NOT NULL,
  `que_opt_b` varchar(255) NOT NULL,
  `que_opt_c` varchar(255) NOT NULL,
  `que_opt_d` varchar(255) NOT NULL,
  `que_correct` enum('a','b','c','d') NOT NULL COMMENT 'เฉลย',
  `que_score` int(11) DEFAULT 1 COMMENT 'คะแนนของข้อนี้'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;

