INSERT INTO `simulations` (`sim_id`, `sim_title`, `sim_desc`, `sim_file_path`, `sim_created_at`) VALUES
(1, 'Lab 1: จับผิด Phishing Email', 'คุณจะแยกออกหรือไม่ว่าอีเมลฉบับไหนเป็นของจริง หรือของปลอมที่แฮกเกอร์ส่งมา?', 'phishing', '2025-12-01 14:30:10'),
(2, 'Lab 2: เจาะระบบด้วย SQL Injection', 'ภารกิจ: ล็อกอินเข้าสู่ระบบ Admin ให้ได้ โดยที่คุณไม่รู้รหัสผ่าน แต่ต้องใช้ช่องโหว่ของภาษา SQL แทน', 'sqli_login', '2025-12-14 17:13:55'),
(3, 'Lab 3: Cross-Site Scripting (XSS)', 'ภารกิจ: ฝังโค้ดอันตรายลงในหน้าเว็บสมุดเยี่ยมชม เพื่อขโมย Cookie ของผู้ใช้งานคนอื่น', 'xss_playground', '2025-12-14 17:47:15'),
(4, 'Lab 4: Path Traversal & LFI', 'ภารกิจ: เจาะระบบแกลเลอรี่รูปภาพ โดยใช้เทคนิค Path Traversal (../) เพื่อขโมยไฟล์ความลับของ Server Linux (/etc/passwd)', 'lfi_gallery', '2025-12-14 18:00:51'),
(5, 'Lab 5: Remote File Inclusion (RFI)', 'ภารกิจ: หลอกให้เว็บไซต์ไปดาวน์โหลดไฟล์ phpinfo.php จากเครื่อง Server ของแฮกเกอร์มารันบนหน้าเว็บ', 'rfi_notes', '2025-12-14 18:18:51'),
(6, 'Lab 6: Password Brute Force', 'ภารกิจ: ปลดล็อกไฟล์ปริศนา โดยการใช้ Python Script ยิงรหัสผ่าน (Brute Force) จนกว่าจะเจอ PIN 4 หลักที่ถูกต้อง', 'brute_force', '2025-12-23 07:50:21'),
(7, 'Lab 7: Command Injection', 'ภารกิจ: เว็บนี้มีเครื่องมือ Ping ทดสอบอินเทอร์เน็ต ลองหาทางแทรกคำสั่ง OS เพื่อดูรายชื่อไฟล์ใน Server (ls)', 'cmd_injection', '2025-12-25 06:55:48'),
(8, 'Lab 8: IDOR (แอบดูข้อมูล)', 'ภารกิจ: คุณกำลังดูโปรไฟล์ของตัวเองอยู่ ลองเปลี่ยนค่า ID ใน URL เพื่อแอบดูเงินเดือนของ CEO ดูสิ', 'idor_profile', '2025-12-25 06:55:48'),
(9, 'Lab 9: Web Shell Upload & Execute', 'ภารกิจ: สวมบทแฮกเกอร์เขียนโค้ด PHP Web Shell ด้วยตัวเอง อัปโหลดขึ้น Server และสั่งรันคำสั่ง System Command ผ่าน URL เพื่อยึดครองระบบ', 'upload_hack', '2025-12-25 06:55:48'),
(10, 'Lab 10: Cookie Manipulation', 'ภารกิจ: เว็บไซต์ใช้ Cookie ระบุสถานะ User ลองแก้ Cookie เพื่อหลอก Server ว่าเราคือ Admin', 'cookie_hack', '2025-12-25 06:55:48'),
(11, 'Lab 11: Business Logic Flaw', 'ภารกิจ: ซื้อสินค้าลับระดับ Super Rare ราคา 100,000 บาท ด้วยงบที่มีเพียง 100 บาท โดยการดักจับและแก้ไขพารามิเตอร์ราคาสินค้า (Parameter Tampering)', 'logic_flaw', '2026-01-07 21:40:50'),
(12, 'Lab 12: EternalBlue (MS17-010)', 'ภารกิจระดับสูง: เจาะระบบ Windows 7 ผ่านช่องโหว่ SMB, ฝัง Backdoor และขโมยรหัสผ่านด้วย Metasploit Framework', 'eternalblue_sim', '2026-01-27 13:00:21');

